INSERT INTO interpretation_fragments (fragment_code, content, tone, priority) VALUES
('TU_VI_TU_TUC_MIEU', 
'Tử Vi miếu địa tại cung Tử Tức thể hiện vai trò của sao này trong việc định hình quan hệ với con cái và cách thể hiện trong đời sống gia đình. Tử Vi chủ về quyền uy và tự chủ, khi ở miếu địa tại cung Tử Tức tạo nên xu hướng nổi bật về khả năng giáo dưỡng và ảnh hưởng đến con cái. Miếu địa là trạng thái mạnh nhất, cho phép Tử Vi phát huy đầy đủ tính chất của nó, tạo nên khả năng thể hiện quyền uy và tự chủ trong việc nuôi dạy con cái. Người có Tử Vi miếu địa tại Tử Tức thường có xu hướng đóng vai trò chủ đạo trong việc giáo dưỡng con cái, với khả năng tạo được ảnh hưởng và định hướng cho con cái. Trong thực tế, họ thường thể hiện tính cách cương nghị, quyết đoán trong việc nuôi dạy, có xu hướng tạo dựng và duy trì cấu trúc trong quan hệ với con cái. Họ có khả năng tạo được vị thế và ảnh hưởng đến con cái, thường có xu hướng đảm nhận trách nhiệm và quyết định các vấn đề quan trọng liên quan đến con cái. Tính cách này thể hiện qua cách họ đối xử với con cái, với xu hướng tạo được sự tôn trọng và ảnh hưởng thông qua sự tự chủ và quyết đoán, đồng thời có khả năng định hướng và giáo dưỡng con cái một cách hiệu quả.', 
'positive', 0),

('TU_VI_TU_TUC_VUONG', 
'Tử Vi vượng địa tại cung Tử Tức thể hiện vai trò của sao này trong việc định hình quan hệ với con cái và cách thể hiện trong đời sống gia đình. Tử Vi chủ về quyền uy và tự chủ, khi ở vượng địa tại cung Tử Tức tạo nên xu hướng tích cực về khả năng giáo dưỡng và ảnh hưởng đến con cái. Vượng địa là trạng thái mạnh, cho phép Tử Vi phát huy tốt các mặt tích cực, tạo nên khả năng thể hiện quyền uy và tự chủ trong việc nuôi dạy con cái. Người có Tử Vi vượng địa tại Tử Tức thường có xu hướng phát triển tốt trong vai trò giáo dưỡng, với khả năng tạo được ảnh hưởng và định hướng cho con cái. Trong thực tế, họ thường thể hiện tính cách độc lập, có khả năng ra quyết định và chịu trách nhiệm trong việc nuôi dạy, với xu hướng tạo dựng và duy trì cấu trúc trong quan hệ với con cái. Họ có khả năng tạo được vị thế và ảnh hưởng đến con cái, thường có xu hướng đảm nhận trách nhiệm và quyết định các vấn đề quan trọng. Tính cách này thể hiện qua cách họ đối xử với con cái, với xu hướng tạo được sự tin tưởng và ảnh hưởng thông qua sự tự chủ và quyết đoán, đồng thời có khả năng định hướng và giáo dưỡng con cái một cách hiệu quả.', 
'positive', 0),

('TU_VI_TU_TUC_DAC', 
