INSERT INTO interpretation_fragments (fragment_code, content, tone, priority) VALUES
('TU_VI_THIEN_DI_MIEU', 
'Tử Vi miếu địa tại cung Thiên Di thể hiện vai trò của sao này trong việc định hình di chuyển, du lịch và cách thể hiện trong các hoạt động di động. Tử Vi chủ về quyền uy và tự chủ, khi ở miếu địa tại cung Thiên Di tạo nên xu hướng nổi bật về khả năng di chuyển với địa vị và ảnh hưởng, có khả năng tạo được vị thế trong các hoạt động di động. Miếu địa là trạng thái mạnh nhất, cho phép Tử Vi phát huy đầy đủ tính chất của nó, tạo nên khả năng thể hiện quyền uy và tự chủ trong việc di chuyển và du lịch. Người có Tử Vi miếu địa tại Thiên Di thường có xu hướng di chuyển với địa vị và ảnh hưởng, với khả năng tạo được vị thế trong các hoạt động di động và du lịch. Trong thực tế, họ thường thể hiện tính cách cương nghị, quyết đoán trong việc di chuyển, có xu hướng tạo dựng và duy trì cấu trúc trong các hoạt động di động. Họ có khả năng tạo được vị thế và ảnh hưởng trong việc di chuyển, thường có xu hướng đảm nhận trách nhiệm và quyết định các vấn đề quan trọng liên quan đến di chuyển và du lịch. Tính cách này thể hiện qua cách họ tiếp cận các hoạt động di động, với xu hướng tạo được sự tôn trọng và ảnh hưởng thông qua sự tự chủ và quyết đoán, đồng thời có khả năng tạo dựng và duy trì các mối quan hệ trong quá trình di chuyển.', 
'positive', 0),

('TU_VI_THIEN_DI_VUONG', 
